mapper.insert:
insert into {{.TableName}} (
    {{- range $i,$x := .Columns -}}
    {{if $i}}, {{end}}{{$x.ColumnName}}
    {{- end -}}
 )
values (
  {{- range $i,$x := .Columns -}}
    {{- if $i}}, {{end -}}
    {{- if eq $x.LowerCamelName "gmtCreate" -}}
    now()
    {{- else if eq $x.LowerCamelName "gmtModified" -}}
    now()
    {{- else if eq $x.LowerCamelName "rowVersion" -}}
    0
    {{- else if eq $x.LowerCamelName "rowStatus" -}}
    0
    {{- else -}}
    #{ {{- $x.LowerCamelName -}} }
    {{- end -}}
    {{- end -}}
)

mapper.update:
update {{.TableName}}

    GMT_MODIFIED = now(),
    ROW_VERSION = ROW_VERSION + 1,
    {{- range .Columns -}}
    {{ if  or (eq .ColumnKey "PRI") (eq .LowerCamelName "rowVersion") (eq .LowerCamelName "gmtModified") (eq .LowerCamelName "gmtCreate") | not }}

       {{.ColumnName}} = #{ {{- .LowerCamelName -}} },

    {{- end}}
    {{end}}

where ID = #{id}
and ROW_STATUS = 0
and ROW_VERSION = #{rowVersion}

mapper.select:
select

from {{.TableName}}
where ROW_STATUS = 0
{{- range .Columns -}}
{{- if  or (eq .ColumnKey "PRI") (eq .LowerCamelName "rowVersion") | not -}}

   and {{.ColumnName}} = #{ {{- .LowerCamelName -}} }

{{- end}}
{{end}}

    limit #{offset},#{pageSize}

select
count(1)
from {{.TableName}}
where
ROW_STATUS = 0
{{- range .Columns -}}
{{- if  or (eq .ColumnKey "PRI") (eq .LowerCamelName "rowVersion") | not -}}

   and {{.ColumnName}} = #{ {{- .LowerCamelName -}} }

{{- end}}
{{end}}

select

from {{.TableName}}
where ROW_STATUS = 0 and id = #{id}
